        justify-content: space-between;

        padding: 5vw 8.7vw;

        h1 {
            font-size: 1.5vw;

            letter-spacing: .125vw;

            text-transform: uppercase;

            color: #fff;
        }

        hr {
            width: 50%;

            margin: 1vw 0;

            border: none;
            border-top: .3vw solid #9c1c28;

            transition: width .2s ease-in-out;
        }

        a {
            display: block;

            margin: 1.07vw 0;

            font-weight: 900;

            text-decoration: none;

            color: #9c1c28;

            transition: color .2s ease-in-out;
        }

        .newsletter {
            width: 29.5%;

            hr {
                width: 25%
            }

            p {
                color: #fff
            }

            input[type="email"] {
                display: block;

                width: 100%;

                padding: .7vw;
                margin-top: 1vw;

                color: #fff;

                border: none;
                border-radius: .1vw;

                background-color: #282828;
            }

            button {
                display: block;

                padding: 0.5vw 1.8vw;
                margin-top: 2.2vw;

                letter-spacing: .1vw;

                text-transform: uppercase;

                font-size: 1.2vw;
                color: #fff;

                background-color: #9c1c28;

                border: none;
                border-radius: 10vw;

                cursor: pointer;

                transition: background-color .2s ease-in-out;
            }
        }
    }
}

// Hover styles

header {
    .header-top {
        .element:hover {
            a {
                color: #dc0016;
            }

            svg {
                fill: #dc0016;
            }
        }
    }

    .header-middle {
        .additional-elements {
            .element:hover {
                svg {
                    fill: #dc0016;
                }
            }
        }
    }

    .header-bottom {
        a:hover {
            background: linear-gradient(270deg, #ff3448, #fff);

            background-size: 50vw 20vw;
            background-position: 8vw;

            background-clip: text;
            -o-background-clip: text;
            -ms-background-clip: text;
            -moz-background-clip: text;
            -webkit-background-clip: text;

            -webkit-text-fill-color: transparent;
        }
    }
}

main {
    .items {
        .item:hover {
            &.white {
                background-color: #aa1927;
            }

            &.black {
                background-color: #8d1824;
            }

            h2 {
                color: #fff
            }

            img {
                transform: scale(1.1);
            }
        }
    }

    .featured-products {
        .special-offers {
            button:hover {
                background-color: #8d1824;
            }

            button:active {
                background-color: #700e18;
            }
        }
    }
}

footer {
    .quicklink {
        a:hover {
            color: #ff3448;
        }
    }

    .quicklink:hover {
        hr {
            width: 100%
        }
    }

    .newsletter {
        button:hover {
            background-color: #8d1824;
        }

        button:active {
            background-color: #700e18;
        }
    }

    .newsletter:hover {
        hr {
            width: 100%
        }
    }
}
